import React, { useEffect, useState } from "react";

export default function AdminCareers({ goBack }) {

  const [careers, setCareers] = useState([]);
  const token = localStorage.getItem("token");

  useEffect(() => {
    fetchCareers();
  }, []);

  const fetchCareers = async () => {
    const res = await fetch("http://localhost:8080/api/admin/careers", {
      headers: { Authorization: `Bearer ${token}` }
    });
    const data = await res.json();
    setCareers(data);
  };

  const deleteCareer = async (id) => {
    await fetch(`http://localhost:8080/api/admin/careers/${id}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` }
    });
    fetchCareers();
  };

  return (
    <div className="p-10">
      <button onClick={goBack} className="mb-6 text-blue-600">
        ‚Üê Back
      </button>

      <h2 className="text-2xl font-bold mb-6">Manage Careers</h2>

      <table className="w-full bg-white shadow rounded-lg">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-3">ID</th>
            <th>Career Name</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {careers.map(c => (
            <tr key={c.id} className="text-center border-t">
              <td className="p-3">{c.id}</td>
              <td>{c.name}</td>
              <td>
                <button
                  onClick={() => deleteCareer(c.id)}
                  className="bg-red-500 text-white px-3 py-1 rounded"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
